import React, { useEffect, useState } from "react";
import { db } from "../firebase";
import { collection, onSnapshot } from "firebase/firestore";
import "mapbox-gl/dist/mapbox-gl.css";
import MapGL, { Marker, Popup } from "react-map-gl";

function StudentPage() {
  const [location, setLocation] = useState(null);
  const [viewport, setViewport] = useState({
    latitude: 0,
    longitude: 0,
    zoom: 15,
  });

  useEffect(() => {
    const unsub = onSnapshot(collection(db, "locations"), (snapshot) => {
      const locations = snapshot.docs.map(doc => doc.data());
      if (locations.length > 0 && locations[0].lat && locations[0].lng) {
        setLocation(locations[0]);
        setViewport((prev) => ({
          ...prev,
          latitude: locations[0].lat,
          longitude: locations[0].lng,
        }));
      }
    });

    return () => unsub();
  }, []);

  const getCursor = () => 'pointer'; // üëà esta es la funci√≥n que faltaba

  return (
    <div style={{ textAlign: "center" }}>
      <h2>Mapa de Ruta</h2>
      {location ? (
        <div style={{ height: "500px", width: "80%", margin: "auto" }}>
          <MapGL
            {...viewport}
            width="100%"
            height="100%"
            mapboxApiAccessToken="pk.eyJ1IjoibHVpczIwMDciLCJhIjoiY205eWt0YzRuMWhpdTJycHYxdDIyeHU3eSJ9.2WqrFQSq-S1n0V5twcWM6A"
            getCursor={getCursor}
            onViewportChange={(viewport) => setViewport(viewport)}
            mapStyle="mapbox://styles/mapbox/streets-v11"
          >
            <Marker latitude={location.lat} longitude={location.lng}>
              <Popup latitude={location.lat} longitude={location.lng}>
                Ruta en movimiento üìç
              </Popup>
            </Marker>
          </MapGL>
        </div>
      ) : (
        <p>Esperando ubicaci√≥n del conductor...</p>
      )}
    </div>
  );
}

export default StudentPage;
