import { useState, useEffect } from "react";
import { db } from "../firebase";
import { doc, setDoc } from "firebase/firestore";
import { MapContainer, TileLayer, Marker, Popup, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";

function DriverPage() {
  const [routeId, setRouteId] = useState("");
  const [tracking, setTracking] = useState(false);
  const [location, setLocation] = useState(null);

  useEffect(() => {
    let watchId;

    if (tracking) {
      if ("geolocation" in navigator) {
        watchId = navigator.geolocation.watchPosition(
          async (position) => {
            const { latitude, longitude } = position.coords;
            setLocation({ lat: latitude, lng: longitude });

            // Guardar siempre en el mismo documento para la ruta
            const locationRef = doc(db, "locations", routeId);
            await setDoc(locationRef, {
              lat: latitude,
              lng: longitude,
              timestamp: Date.now(),
            });

            console.log("Ubicación enviada:", latitude, longitude);
          },
          (error) => {
            console.error("Error obteniendo ubicación", error);
          },
          {
            enableHighAccuracy: true,
            maximumAge: 10000,
            timeout: 5000,
          }
        );
      } else {
        alert("La geolocalización no está disponible en este dispositivo.");
      }
    }

    return () => {
      if (watchId) navigator.geolocation.clearWatch(watchId);
    };
  }, [tracking, routeId]);

  const startTracking = () => {
    if (!routeId) {
      alert("Por favor ingresa el identificador de la ruta.");
      return;
    }
    setTracking(true);
  };

  function UpdateMapCenter({ lat, lng }) {
    const map = useMap();
    useEffect(() => {
      map.setView([lat, lng], 15);
    }, [lat, lng, map]);
    return null;
  }

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>Modo Conductor 🚗</h2>
      <input
        type="text"
        placeholder="Identificador de Ruta"
        value={routeId}
        onChange={(e) => setRouteId(e.target.value)}
        style={{ padding: "10px", width: "250px" }}
        disabled={tracking}
      />
      <br /><br />
      <button onClick={startTracking} style={{ padding: "10px 20px" }} disabled={tracking}>
        {tracking ? "Enviando ubicación..." : "Iniciar Ruta"}
      </button>

      {location && (
        <div style={{ marginTop: "30px" }}>
          <MapContainer center={[location.lat, location.lng]} zoom={15} style={{ height: "400px", width: "80%", margin: "auto" }}>
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
            <Marker position={[location.lat, location.lng]}>
              <Popup>Estás aquí 🚗</Popup>
            </Marker>
            <UpdateMapCenter lat={location.lat} lng={location.lng} />
          </MapContainer>
        </div>
      )}
    </div>
  );
}

export default DriverPage;
